import {ScrollTimeline} from "https://flackr.github.io/scroll-timeline/dist/scroll-timeline.js";

const scrllTrckr = document.querySelector(".scrollTracker");
const scrllTrckngTmle = new ScrollTimeline({
    source: document.scrollingElement,
    orientation: "block",
    scrollOffsets: [CSS.percent(0), CSS.percent(100)],
});

scrllTrckr.animate(
    {
        transform: ['scaleX(0)', 'scaleX(1)']
    },
    {
        duration: 1,
        timeline: scrllTrckngTmle,
    },
);
