import {Canvas, ReactThreeFiber} from '@react-three/fiber';
// import { meshStandardMaterial } from '@react-three/drei';
import { shaderMaterial } from '@react-three/drei';
import glsl from "babel-plugin-glsl/macro";

import './App.css';

const WaveyShaderMaterial = shaderMaterial (
  // uniform
  {},
  // vertex 
  glsl``,
  // fragment
  glsl``,
);

extend( WaveyShaderMaterial );

const Scene = () => {
  return <Canvas>
    <pointLight position={[10,100,100]} />
    <mesh>
      <planeBufferGeometry args={[3, 5]} />
      <waveyShaderMaterial />
    </mesh>
  </Canvas>
}

const App = () => {
  return <Scene />
}

export default App;
